import Head from 'next/head'
import { useRouter } from 'next/router'


export default function Home() {
    const router = useRouter()
  function firstScript() {
    fetch('http://127.0.0.1:3001/autohospot_internet').then(response => {
      return response;})
  }
  function secondScript() {
    fetch('http://127.0.0.1:3001/autohotspot_direct').then(response => {
      return response;})
  }
  function thirdScript() {
    fetch('http://127.0.0.1:3001/static_hotspot').then(response => {
      return response;})
  }
  function fourthScript() {
    fetch('http://127.0.0.1:3001/remove_autohotspot_or_static_hotspot').then(response => {
      return response;})
  }
  function fifthScript() { // Routing to be added in this Button
        router.push()
/*    fetch('http://127.0.0.1:3001/change_add_wifi_network').then(response => {
      return response;})*/
  }
  function sixthScript() {
    fetch('http://127.0.0.1:3001/force_hotspot_force_network').then(response => {
      return response;})
  }
  function seventhScript() {
    fetch('http://127.0.0.1:3001/change_hotspot_sSID_and_password').then(response => {
      return response;})

  }
  let styleConfig = { backgroundimage : '/abc.jpeg' }


  return (

      
    <div className="container"  style={styleConfig} >
      <Head>
        <title>Rasberry-pi-wifi-setup</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
    
      <main>
      

        <h1 className="title">
          Respberry Pie Auto Wifi!
        </h1>



        <div className="grid" >
            <button  className="card" onClick={firstScript}>Autohospot Internet</button>
            <button className="card" onClick={secondScript}>Autohotspot Direct</button>
            <button  className="card" onClick={thirdScript}>Static Hotspot</button>
            <button className="card" onClick={fourthScript}>Remove Autohotspot or Static Hotspot</button>
            <Link href="/wifi">
            <button className="card" >Change/Add Wifi Network</button></Link>
            <button className="card" onClick={sixthScript}>Force Hotspot</button>
            <button className="card" onClick={seventhScript}>Change Hotspot SSID and Password</button>
        </div>

      </main>


      <style jsx>{`

        .container {
          back  
          min-height: 100vh;
          padding: 0 0.5rem;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;

        }
        

        main {
 
          padding: 5rem 0;
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        
        }

        footer {
          width: 100%;
          height: 100px;
          border-top: 1px solid #eaeaea;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        footer img {
          margin-left: 0.5rem;
        }

        footer a {
          display: flex;
          justify-content: center;
          align-items: center;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        .title a {
          color: #0070f3;
          text-decoration: none;
        }

        .title a:hover,
        .title a:focus,
        .title a:active {
          text-decoration: underline;
        }

        .title {
          margin: 0;
          line-height: 1.15;
          font-size: 4rem;
        }

        .title,
        .description {
          text-align: center;
        }

        .description {
          line-height: 1.5;
          font-size: 1.5rem;
        }

        code {
          background: #fafafa;
          border-radius: 5px;
          padding: 0.75rem;
          font-size: 1.1rem;
          font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
            DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
        }

        .grid {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;

          max-width: 800px;
          margin-top: 3rem;
        }

        .card {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .card:hover,
        .card:focus,
        .card:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .card h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p {
          margin: 0;
          font-size: 1.25rem;
          line-height: 1.5;
        }

        .logo {
          height: 1em;
        }
        

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }

        
      `}</style>



      <style jsx global>{`
        html,
        body {

          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  )
}
